        public static void PrintMap()
        {
            for (int i = 0; i < Map.GetLength(0); i++)
            {
                for (int j = 0; j < Map.GetLength(1); j++)
                {
                    Console.Write(Map[i, j]);
                }
                Console.WriteLine();
            }
            Console.SetCursorPosition(0, 0);
            MapBefore = (char[,])Map.Clone();
        }

        public static void JumpPlayer()
        {
            if (!Game.ballIsFlight)
            {
                for (int i = Game.MinYOfBall; i < Game.MaxYOfBall; i++)
                {
                    for (int j = Game.MIN_X_OF_BALL; j < Game.MAX_X_OF_BALL; j++)
                    {
                        Map[i - Game.HeightOfJump, j] = Map[i, j];
                        Map[i, j] = ' ';
                    }
                }
              
               
            }
        }

        public static void FallBall()
        {
            if(Map[28,1] == '-' && Game.ballIsFlight)
            {
                Game.ballIsFlight = false;
              
                return;
            }
            for (int i = Game.MinYOfBall; i < Game.MaxYOfBall; i++)
            {
                for (int j = Game.MIN_X_OF_BALL; j < Game.MAX_X_OF_BALL; j++)
                {
                    Map[i + 1, j] = Map[i, j];
                    Map[i, j] = ' ';
                }
            }
            for (int i = Game.MIN_X_OF_BALL; i <= Game.MAX_X_OF_BALL; i++)
            {
                Map[Game.MaxYOfBall, i] = '*';
            }
           
        }
        public static void MoveBlocks()
        {
            MapBefore = (char[,])Map.Clone();
            for (int i = 0; i < Map.GetLength(0) - 1; i++)
            {
                for (int j = 0; j < Map.GetLength(1); j++)
                {
                    if(Map[i,j] == '*')
                    {
                        Map[i, j - 1] = '*';
                        Map[i, j] = ' ';
                    }
                }
            }
        }

    }
